import React from 'react'
import personService from '../services/person'

const Persons = ({ showPerson, setPersons, setMessage }) => {

    const isDelete = (person) => {
    const result = window.confirm(`Delete ${person.name}`)
        if (result) {
            personService
                .deletePerson(person.id)
                .then(response => {
                    setPersons(showPerson.filter(item => item !== person))
                    setMessage({
                        text: `${person.name} is removed`,
                        type: "success",
                    })
                })
                .catch((error) => {
                    setMessage({
                        text: `${person.name} was already removed from server`,
                        type: "extra",
                    })
                })
        }
    }

    return (
        showPerson.map((event) =>
          <div key={event.name}>
                {event.name}
                {" "}
                {event.number}
                {" "}
                <button onClick={() => isDelete(event)}>delete</button>
            </div>
        )
    )
}

export default Persons
