import React from 'react'
import personService from '../services/persons'

const Persons = ({newFilter, persons, setPersons, setMessage}) =>  //setStyle
{
  

  const handleClick = (id) => {
    const thisPerson = persons.find(p => p.id === id )
    if (window.confirm(`Delete ${thisPerson.name} ?`)) {
      personService
        .deletePerson(id)
        .then(() => {
          setMessage (`Deleted ${thisPerson.name}`)
          //setStyle("red")
          
          return setPersons(persons.filter(person => person.id !== id))
        })
    } 
  }

  if (newFilter==='') {
    return persons.map(person => {
      return (
        <div key={person.id}>
          {person.name} {person.number}
          <button onClick={()=> handleClick(person.id)}>delete</button>
        </div>
      )})
  }
  else {
    return persons
      .filter(person=>person.name.toLowerCase().includes(newFilter.toLowerCase()))
      .map((person, index) => {
        return (
            <div key={person.id}>
              {person.name} {person.number}
              <button onClick={()=> handleClick(index)}>delete</button>
            </div>
        )
      })
  }
}
export default Persons
