import React from 'react'
import personService from '../services/person'

const Persons = ({ showPerson, setPersons, setMessage }) => {

    const toDelete = (person) => {
    const result = window.confirm(`Delete ${person.name}?`)
        if (result) {
            personService
                .deletePerson(person.id)
                .then(response => {
                    setPersons(showPerson.filter(item => item !== person))
                    setMessage({
                        text: `Removed ${person.name} from phonebook`,
                        type: "success",
                    })
                })
                .catch((error) => {
                    setMessage({
                        text: `Information of ${person.name} has already been removed from server`,
                        type: "fail",
                    })
                })
        }
    }

    return (
        showPerson.map((person) =>
          <div key={person.name}>
            {person.name} {person.number} {" "}
                <button onClick={() => toDelete(person)}>delete</button>
            </div>
        )
    )
}

export default Persons
